                map.get(level1).get(level2).get(level3).add("data 2");
            }
        }else{
            map.get(level1).put(level2, new HashMap<>());
            if(map.get(level1).get(level2).containsKey(level3)){
                map.get(level1).get(level2).get(level3).add("data 1");
